;(function($){

	wp.customize( 'header_textcolor', function( value ) {
		value.bind( function( newval ) {
			$('#header a, .site-title a, .site-description').css('color', newval );
		});
	});

	wp.customize( 'link_color', function( value ) {
		value.bind( function( newval ) {
			$('a').css('color', newval );
		});
	});

	wp.customize( 'text_color', function( value ) {
		value.bind( function( newval ) {
			$('body').css('color', newval );
		});
	});

	wp.customize( 'body_bg_color', function( value ) {
		value.bind( function( newval ) {
			$('body').css('background-color', newval );
		});
	});

	wp.customize( 'body_bg_img', function( value ) {
		value.bind( function( newval ) {
			$('body').css('background-image', 'url(' + newval + ')' );
		});
	});

	wp.customize( 'site-logo_image', function( value ) {
		value.bind( function( newval ) {
			var $siteLogoImg = $('.site-logo img');
			if ( $siteLogoImg.length > 0 ) {
				$siteLogoImg.remove();
			}
			if ( '' != newval ) {
				$('.site-logo span').hide();
				$('.site-logo a').prepend('<img src="' + newval + '" />');
			} else {
				$('.site-logo span').show();
			}
		});
	});

	wp.customize( 'body_bg_img_repeat', function( value ) {
		value.bind( function( newval ) {
			$('body').css( { 'background-repeat': newval, 'background-size': '' } );
		});
	});

	wp.customize( 'body_font', function( value ) {
		value.bind( function( newval ) {
			$('body').css('font-family', newval );
		});
	});

})(jQuery);